t class="form-control" type="text" id="date" name="date" placeholder="{{ trans('site.date') }}" value="{{ date('Y-m-d') }}" readonly />                                    <span class="input-group-addon"><img src="{{ Request::root() }}/design/site/img/calendar.png" alt="calendar"></span>                                </div>                            </div>                        </div>                        <div class="rangeDiv wow zoomIn " data-wow-delay="1.4s" >                            <span class="minRange">{{ $min_price . ' '  . trans('site.rs') }}</span>                            <span class="maxRange">{{ $max_price . ' '  . trans('site.rs') }}</span>                            <label for="partyRange">{{ trans('site.event_price') }} <label id="eventPrice">: {{ $min_price . '  ' . trans('site.rs') }}</label></label>                            <input                                    name="price"                                    type="range"                                    min="{{ $min_price }}"                                    max="{{ $max_price }}"                                    value="{{ $min_price }}"                                    step="{{ ($max_price - $min_price) / $min_price }}"                                    id="partyRange"                            >                        </div>                    </form>                    <div class="events">                        <div class="row" id="events">                            @foreach($events as $event)                                <div class="col-md-4">                                    <a href='{{ route('event', $event->id) }}' class="eventDet">                                        <img src="{{ Request::root() }}/images/events/{{ $event->images()->first()->name }}" alt="event Img">                                        <span class="date">{{ Carbon\Carbon::parse($event->date)->format('M d') }}</span>                                        <span 