@foreach($events as $event)    <div class="col-md-4">        <a href='{{ route('event', $event->id) }}' class="eventDet">            <img src="{{ Request::root() }}/images/events/{{ $event->images()->first()->name }}" alt="event Img">            <span class="date">{{ Carbon\Carbon::parse($event->date)->format('M d') }}</span>            <span class="name">{{ $event->title }}</span>        </a>    </div>@endforeach